def taxid_to_taxonomy(taxid):
    return dict_tax[str(taxid)]


def get_taxonomy_list(rank, taxa=None):
    if taxa == None:
        dict_rank = {"Domain": 0, "Phylum": 1, "Class": 2,
                     "Order": 3, "Family": 4, "Genus": 5, "Species": 6}
        rank_num = dict_rank[rank]
        taxonomies = dict_tax.values()
        taxonomy_list = list(set([taxonomy.split("/")[rank_num] for taxonomy in taxonomies]))
        return taxonomy_list
    
    else:
        dict_rank = {"Domain": 0, "Phylum": 1, "Class": 2,
                     "Order": 3, "Family": 4, "Genus": 5, "Species": 6}
        rank_num = dict_rank[rank]
        taxonomies = dict_tax.values()
        taxonomies = [i for i in taxonomies if taxa in i]
        taxonomy_list = list(set([taxonomy.split("/")[rank_num] for taxonomy in taxonomies]))
        return taxonomy_list

print(get_taxonomy_list("Order", "Halobacteria"))
print(get_taxonomy_list("Order", "Thermoplasmata"))
print(get_taxonomy_list("Order", "Thermococci"))
print(get_taxonomy_list("Order", "Methanomicrobia"))
